import { useEffect, useState } from "react";
import { Col, Row, Typography } from "antd";
import { collection, getDocs } from "firebase/firestore";
import db from "../firebase/index";

// import logo from "./logo.svg";

const { Title } = Typography;

const App = () => {
  // const [internships, setInternships] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const internships = collection(db, "internships");
    getDocs(internships)
      .then((docs) => {
        // const list = docs.map((doc) => doc.data());
        setLoading(false);
        console.log(`Here is the list ${docs}`, `Loading ${loading}`);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <Row justify="center" align="middle" style={{ height: "100vh" }}>
      <Col span={14}>
        <Title> Initialized Firestore </Title>
      </Col>
      <Col span={14}>
        <Title>{loading ? "Loading ..." : "Internships List Loaded"}</Title>
      </Col>
    </Row>
  );
};

export default App;
