import "./InternshipPost.scss";

import { Col, List, Row } from "antd";

const InternshipPost = (props) => {
  const { post, widths } = props;
  const { role, description, period, dateCreated, totalEnrolled, qualifiedCandidates, id } = post;

  return (
    <List.Item key={id} className="post">
      <Row justify="center" align="middle">
        <Col {...widths.internshipTitle}>
          <List.Item.Meta title={role} description={description} />
        </Col>
        <Col {...widths.completitionPeriod}>
          <List.Item.Meta title={period} description={dateCreated} />
        </Col>
        <Col {...widths.totalEnrolled}>{totalEnrolled}</Col>
        <Col {...widths.qualifiedCandidates}>{qualifiedCandidates}</Col>
        <Col {...widths.icons} />
      </Row>
    </List.Item>
  );
};

export default InternshipPost;
